# 导包import unittestimport loggingfrom parameterized import parameterizedfrom api.login import TestLoginApifrom utils import assert_common, build_datadef Headers():    headers = {"Content-Type": "application/json"}    return headersclass TestIHRMLogin(unittest.TestCase):    # 类方法初始化    @classmethod    def setUpClass(cls):        cls.login_url = "http://ihrm-test.itheima.net" + "/api/sys/login"        cls.login_api = TestLoginApi()        cls.headers = Headers()  # 实例化请求头    @classmethod    def tearDownClass(cls):        ...    # 编写第一个案例,测试登录成功    @parameterized.expand(build_data("../data/login_data.json"))    def test01_login_success(self, case_name, jsonData, http_code, success, code, massage):        # IHRM项目可以直接发送登录请求        headers = self.headers  # 定义请求头        jsonData = jsonData  # json数据        # 发送登录请求        response = self.login_api.login(jsonData, headers)        # 打印登录结果        result = response.json()        logging.info("test01-登录的结果为:{}".format(result))        # 断言        assert_common(self, http_code, success, code, massage, response)