import jsondef assert_common(self,httpcode, success, code, message,response):    self.assertEqual(httpcode, response.status_code)    self.assertEqual(success, response.json().get('success'))    self.assertEqual(code, response.json().get('code'))    self.assertIn(message, response.json().get('message'))def build_data(filename):    empty_list = []    with open(filename,encoding="utf-8") as f:        jsonData = json.load(f)        # print("完整数据：", jsonData)    for login_data in jsonData:        empty_list.append(list(login_data.values()))    print(empty_list)    return empty_list# 1 定义读取员工模块数据的函数, 并从外界接受数据文件路径和要读取def read_emp_data(filename, name):    # 2 使用内置函数open打开外界传入的文件名    with open(filename, 'r', encoding="utf-8") as f:        # 3 使用内置模块json加载文件        jsonData = json.load(f)        # 4 读取数据        emp_data = jsonData.get(name)        # print(list(emp_data.values()))        # 定义存放数据的空列表        result_list = []        # 将数据转化为元组后添加到列表中        result_list.append(tuple(emp_data.values()))        return result_listif __name__ == '__main__':    # build_data("./data/login_data.json")    read_emp_data("./data/emp_data.json", "add_emp")    read_emp_data("./data/emp_data.json", "query_emp")    read_emp_data("./data/emp_data.json", "modify_emp")    read_emp_data("./data/emp_data.json", "delete_emp")